import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

export default function Register() {
  const { register, handleSubmit, watch, formState } = useForm();
  const navigate = useNavigate();

  const collectFormData = async (formData) => {
    // destructure & restructure address
    const { fullAddress, pincode, password2, ...rest } = formData;
    const actualData = { ...rest, address: { fullAddress, pincode } };

    // send API call
    const response = await fetch(
      "http://localhost:8081/api/v1/users/register",
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(actualData),
      }
    );

    const responseObject = await response.json();

    // check response and show toast
    if (responseObject.data === true) {
      Swal.fire({
        toast: true,
        position: "top-end",
        icon: "warning",
        title: "Email Already Exists",
        showConfirmButton: false,
        timer: 3000,
        timerProgressBar: true,
      });
    } else {
      Swal.fire({
        toast: true,
        position: "top-end",
        icon: "success",
        title: "Registration Success",
        showConfirmButton: false,
        timer: 3000,
        timerProgressBar: true,
      });
      navigate("/login");
    }
  };

  return (
    <div className="d-flex justify-content-center">
      <form className="w-25" onSubmit={handleSubmit(collectFormData)}>
        <h1 className="text-center mb-3">Register</h1>

        {/* Name */}
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Your Name"
            {...register("name", {
              required: true,
              minLength: 3,
              maxLength: 10,
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.name?.type === "required" && "Name is required"}
            {formState.errors.name?.type === "minLength" &&
              "Name must have min 3 characters"}
            {formState.errors.name?.type === "maxLength" &&
              "Name should not exceed 10 characters"}
          </div>
        </div>

        {/* Email */}
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="Your Email"
            {...register("email", {
              required: true,
              pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.email?.type === "required" && "Email is required"}
            {formState.errors.email?.type === "pattern" && "Enter valid email"}
          </div>
        </div>

        {/* Password */}
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Your Password"
            {...register("password", {
              required: true,
              minLength: 3,
              maxLength: 10,
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.password?.type === "required" &&
              "Password is required"}
            {formState.errors.password?.type === "minLength" &&
              "Password must have min 3 characters"}
            {formState.errors.password?.type === "maxLength" &&
              "Password should not exceed 10 characters"}
          </div>
        </div>

        {/* Confirm Password */}
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Confirm Password"
            {...register("password2", {
              required: true,
              minLength: 3,
              maxLength: 10,
              validate: (value) =>
                value === watch("password") || "Passwords do not match",
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.password2?.type === "required" &&
              "Confirm Password is required"}
            {formState.errors.password2?.type === "minLength" &&
              "Confirm Password must have min 3 characters"}
            {formState.errors.password2?.type === "maxLength" &&
              "Confirm Password should not exceed 10 characters"}
            {formState.errors.password2?.type === "validate" &&
              formState.errors.password2.message}
          </div>
        </div>

        {/* Address */}
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Your Address"
            {...register("fullAddress", {
              required: true,
              minLength: 5,
              maxLength: 100,
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.fullAddress?.type === "required" &&
              "Address is required"}
            {formState.errors.fullAddress?.type === "minLength" &&
              "Address must have min 5 characters"}
            {formState.errors.fullAddress?.type === "maxLength" &&
              "Address should not exceed 100 characters"}
          </div>
        </div>

        {/* Pincode */}
        <div className="mb-3">
          <input
            type="number"
            className="form-control"
            placeholder="Your Pincode"
            {...register("pincode", {
              required: true,
              minLength: 6,
              maxLength: 6,
            })}
          />
          <div className="form-text text-danger">
            {formState.errors.pincode?.type === "required" &&
              "Pincode is required"}
            {formState.errors.pincode?.type === "minLength" &&
              "Pincode must have exactly 6 digits"}
            {formState.errors.pincode?.type === "maxLength" &&
              "Pincode must have exactly 6 digits"}
          </div>
        </div>

        {/* Gender */}
        <div className="mb-3">
          <select className="form-select" {...register("sex")}>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        {/* User Type */}
        <div className="mb-3">
          <select className="form-select" {...register("userType")}>
            <option value="user">User</option>
            <option value="vendor">Vendor</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-100">
          Submit
        </button>
      </form>
    </div>
  );
}
