import React, { useContext, useState } from "react";
import { useForm } from "react-hook-form";
import Swal from "sweetalert2";
import { LoginContext } from "../context/LoginContext";
import { useNavigate } from "react-router-dom";

const BASE = "http://localhost:8080";

export default function Login() {
  const { register, handleSubmit } = useForm();
  const { setIsLogin, setUserId, setRole, setUserName } =
    useContext(LoginContext);
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const onSubmit = async (form) => {
    setIsSubmitting(true);

    if (form.role === "customer") {
      const res = await fetch(`${BASE}/api/customers/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: form.email, password: form.password }),
      });

      const obj = await res.json();

      if (res.ok) {
        const user = obj.data;
        setIsLogin(true);
        setUserId(user?.id ?? null);
        setRole("customer");
        setUserName(user?.name ?? "");

        sessionStorage.setItem("userId", user?.id ?? "");
        sessionStorage.setItem("userName", user?.name ?? "");
        sessionStorage.setItem("role", "customer");

        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "success",
          title: obj.message || "Login successful",
          showConfirmButton: false,
          timer: 2000,
        });

        navigate("/");
      } else {
        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "error",
          title: obj.message || "Invalid customer credentials",
          showConfirmButton: false,
          timer: 2000,
        });
      }
    }

    if (form.role === "admin") {
      const res = await fetch(`${BASE}/api/admin/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: form.email, password: form.password }),
      });

      const obj = await res.json();

      if (res.ok) {
        const admin = obj.data;
        setIsLogin(true);
        setUserId(admin?.id ?? null);
        setRole("admin");
        setUserName(admin?.name ?? "");

        sessionStorage.setItem("userId", admin?.id ?? "");
        sessionStorage.setItem("userName", admin?.name ?? "");
        sessionStorage.setItem("role", "admin");

        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "success",
          title: obj.message || "Admin login successful",
          showConfirmButton: false,
          timer: 2000,
        });

        navigate("/dashboard");
      } else {
        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "error",
          title: obj.message || "Invalid admin credentials",
          showConfirmButton: false,
          timer: 2000,
        });
      }
    }

    setIsSubmitting(false);
  };

  return (
    <div className="container-fluid">
      <button className="btn btn-outline-dark" onClick={() => navigate("/")}>
        Home
      </button>

      <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
        <div
          className="card p-4"
          style={{ width: "350px", borderRadius: "10px" }}
        >
          <h3 className="text-center mb-3">Login</h3>
          <form onSubmit={handleSubmit(onSubmit)} className="row g-3">
            <div className="col-12">
              <label className="form-label">Login as</label>
              <select className="form-select" {...register("role")} required>
                <option value="">-- Select Role --</option>
                <option value="customer">Customer</option>
                <option value="admin">Admin</option>
              </select>
            </div>

            <div className="col-12">
              <label className="form-label">Email</label>
              <input
                type="email"
                className="form-control"
                placeholder="Enter email"
                {...register("email")}
                required
              />
            </div>

            <div className="col-12">
              <label className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                {...register("password")}
                required
              />
            </div>

            <div className="col-12 d-grid">
              <button
                type="submit"
                className="btn btn-primary"
                disabled={isSubmitting}
              >
                {isSubmitting ? "Logging in..." : "Login"}
              </button>
            </div>

            <div className="col-12 text-center mt-2">
              <p className="mb-0">
                Don't have an account? <a href="/register">Register here</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
