import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  FaUser,
  FaEnvelope,
  FaLock,
  FaPhone,
  FaHome,
  FaBirthdayCake,
  FaDumbbell,
} from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import Swal from "sweetalert2";

export default function Register() {
  const navigate = useNavigate();

  // react-hook-form
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting },
    reset,
  } = useForm();

  const [adminExists, setAdminExists] = useState(null);
  const [role, setRole] = useState("customer"); // "admin" only allowed when no admin exists
  const [message, setMessage] = useState(null);

  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const res = await fetch("http://localhost:8080/api/admin/exists");
        if (!res.ok) {
          setAdminExists(false);
          return;
        }
        const data = await res.json();
        // handle both boolean or {data: boolean}
        const exists =
          typeof data === "boolean" ? data : Boolean(data?.data ?? false);
        setAdminExists(exists);
        if (exists) setRole("customer");
      } catch {
        setAdminExists(false);
      }
    };
    checkAdmin();
  }, []);

  const onSubmit = async (formData) => {
    setMessage(null);

    const url =
      role === "admin"
        ? "http://localhost:8080/api/admin/register"
        : "http://localhost:8080/api/customers/register";

    // build payloads for each role
    const payload =
      role === "admin"
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
          }
        : {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            phone: formData.phone || "",
            address: formData.address || "",
            age:
              formData.age === undefined || formData.age === ""
                ? null
                : Number(formData.age),
            sex: formData.sex || null, // üëà gender goes under 'sex'
          };

    try {
      const res = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        const data = await res.json();
        setMessage(data?.message || "Registered successfully");

        // toast
        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "success",
          title: data?.message || "Registered successfully",
          showConfirmButton: false,
          timer: 2500,
        });

        // reset form fields
        reset();
        if (role === "admin") setAdminExists(true);
      } else {
        const errText = await res.text();
        setMessage(errText || "Registration failed");
        Swal.fire({
          toast: true,
          position: "top-end",
          icon: "error",
          title: errText || "Registration failed",
          showConfirmButton: false,
          timer: 2500,
        });
      }
    } catch {
      setMessage("Server not reachable");
      Swal.fire({
        toast: true,
        position: "top-end",
        icon: "error",
        title: "Server not reachable",
        showConfirmButton: false,
        timer: 2500,
      });
    }
  };

  if (adminExists === null) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status" />
        <span className="ms-2 fw-bold text-primary">Checking server...</span>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <button
        className="btn btn-outline-dark"
        onClick={() => {
          navigate("/");
        }}
      >
        Home
      </button>

      <div className="row justify-content-center">
        <div className="col-sm-10 col-md-8 col-lg-6">
          <div className="card shadow-lg border-0 rounded-4">
            <div className="card-body p-5">
              <h3 className="card-title mb-4 text-center fw-bold text-primary">
                <FaDumbbell className="me-2" />
                Gym Registration
              </h3>

              {/* Role Selection (only when NO admin exists) */}
              {!adminExists && (
                <div className="mb-4">
                  <label className="form-label fw-semibold">Register as</label>
                  <select
                    className="form-select"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                  >
                    <option value="admin">üèãÔ∏è Admin</option>
                    <option value="customer">üí™ Customer</option>
                  </select>
                </div>
              )}

              <form onSubmit={handleSubmit(onSubmit)} className="row g-3">
                {/* Name */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaUser className="me-2" />
                    Full Name
                  </label>
                  <input
                    className="form-control"
                    placeholder="John Doe"
                    {...register("name", {
                      required: "Name is required",
                      minLength: { value: 3, message: "Min 3 characters" },
                      maxLength: { value: 50, message: "Max 50 characters" },
                    })}
                  />
                  <div className="form-text text-danger">
                    {errors.name?.message}
                  </div>
                </div>

                {/* Email */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaEnvelope className="me-2" />
                    Email
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    placeholder="example@gym.com"
                    {...register("email", {
                      required: "Email is required",
                      pattern: {
                        value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                        message: "Enter valid email",
                      },
                    })}
                  />
                  <div className="form-text text-danger">
                    {errors.email?.message}
                  </div>
                </div>

                {/* Password */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaLock className="me-2" />
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Choose a strong password"
                    {...register("password", {
                      required: "Password is required",
                      minLength: { value: 6, message: "Min 6 characters" },
                      maxLength: { value: 100, message: "Max 100 characters" },
                    })}
                  />
                  <div className="form-text text-danger">
                    {errors.password?.message}
                  </div>
                </div>

                {/* Confirm Password (client check only) */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    Confirm Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Re-enter password"
                    {...register("password2", {
                      required: "Confirm Password is required",
                      validate: (v) =>
                        v === watch("password") || "Passwords do not match",
                    })}
                  />
                  <div className="form-text text-danger">
                    {errors.password2?.message}
                  </div>
                </div>

                {/* Customer-only fields */}
                {(adminExists || role === "customer") && (
                  <>
                    {/* Phone */}
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaPhone className="me-2" />
                        Phone
                      </label>
                      <input
                        className="form-control"
                        placeholder="1234567890"
                        {...register("phone", {
                          required: "Phone is required",
                          pattern: {
                            value: /^[0-9]{10}$/,
                            message: "Enter 10-digit number",
                          },
                        })}
                      />
                      <div className="form-text text-danger">
                        {errors.phone?.message}
                      </div>
                    </div>

                    {/* Address */}
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaHome className="me-2" />
                        Address
                      </label>
                      <input
                        className="form-control"
                        placeholder="Street, City"
                        {...register("address", {
                          required: "Address is required",
                          minLength: { value: 3, message: "Min 3 characters" },
                          maxLength: {
                            value: 200,
                            message: "Max 200 characters",
                          },
                        })}
                      />
                      <div className="form-text text-danger">
                        {errors.address?.message}
                      </div>
                    </div>

                    {/* Age */}
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaBirthdayCake className="me-2" />
                        Age
                      </label>
                      <input
                        type="number"
                        min="0"
                        className="form-control"
                        placeholder="Age"
                        {...register("age", {
                          required: "Age is required",
                          min: { value: 1, message: "Age must be > 0" },
                          max: { value: 120, message: "Age looks too high" },
                        })}
                      />
                      <div className="form-text text-danger">
                        {errors.age?.message}
                      </div>
                    </div>

                    {/* Sex (Gender) */}
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">Gender</label>
                      <select
                        className="form-select"
                        {...register("sex", { required: "Gender is required" })}
                      >
                        <option value="">Select Gender</option>
                        <option value="MALE">Male</option>
                        <option value="FEMALE">Female</option>
                        <option value="OTHER">Other</option>
                      </select>
                      <div className="form-text text-danger">
                        {errors.sex?.message}
                      </div>
                    </div>
                  </>
                )}

                {/* Submit */}
                <div className="col-12">
                  <button
                    type="submit"
                    className="btn btn-primary w-100 py-2 fw-semibold"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? (
                      <>
                        <span
                          className="spinner-border spinner-border-sm me-2"
                          role="status"
                          aria-hidden="true"
                        ></span>
                        Registering...
                      </>
                    ) : (
                      `Register ${
                        adminExists || role === "customer"
                          ? "Customer"
                          : "Admin"
                      }`
                    )}
                  </button>
                </div>

                {/* Login Redirect */}
                <div className="text-center mt-3">
                  <p className="mb-0">
                    Already have an account?{" "}
                    <a href="/login" className="fw-bold text-decoration-none">
                      Login here
                    </a>
                  </p>
                </div>
              </form>

              {/* Message area (kept like your original) */}
              {message && (
                <div className="mt-4">
                  <div className="alert alert-info text-center fw-semibold">
                    {message}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
