import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Register() {
  const navigate = useNavigate();
  const [adminExists, setAdminExists] = useState(null);
  const [role, setRole] = useState("customer");

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors, isSubmitting },
  } = useForm();

  // üîπ check if admin already exists
  useEffect(() => {
    const checkAdmin = async () => {
      const res = await fetch("http://localhost:8080/api/admin/exists");
      if (!res.ok) {
        setAdminExists(false);
        return;
      }
      const data = await res.json();
      if (typeof data === "boolean") {
        setAdminExists(data);
        if (data) setRole("customer");
      } else if (typeof data?.data !== "undefined") {
        setAdminExists(data.data);
        if (data.data) setRole("customer");
      } else {
        setAdminExists(false);
      }
    };
    checkAdmin();
  }, []);

  // üîπ handle register submit
  const onSubmit = async (formData) => {
    const url =
      role === "admin"
        ? "http://localhost:8080/api/admin/register"
        : "http://localhost:8080/api/customers/register";

    const payload =
      role === "admin"
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
          }
        : {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            phone: formData.phone,
            address: formData.address,
            age: formData.age ? Number(formData.age) : null,
            sex: formData.sex,
          };

    const res = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    if (res.ok) {
      const data = await res.json();
      Swal.fire(
        "Success",
        data?.message || "Registered successfully",
        "success"
      );
      reset();
      if (role === "admin") setAdminExists(true);
    } else {
      const errText = await res.text();
      Swal.fire("Error", errText || "Registration failed", "error");
    }
  };

  // üîπ handle validation errors with SweetAlert
  const onError = (errors) => {
    const firstErrorKey = Object.keys(errors)[0];
    if (firstErrorKey) {
      Swal.fire(
        "Validation Error",
        errors[firstErrorKey]?.message || "Invalid input",
        "error"
      );
    }
  };

  if (adminExists === null) {
    return <p className="text-center mt-5">Checking server...</p>;
  }

  return (
    <div className="container mt-4 w-25">
      <button className="btn btn-link mb-3" onClick={() => navigate("/")}>
        ‚Üê Home
      </button>

      <div className="card shadow-sm p-4">
        <h2 className="text-center mb-3">Register</h2>

        {!adminExists && (
          <div className="mb-3">
            <label className="form-label">Register as:</label>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="form-select"
            >
              <option value="admin">Admin</option>
              <option value="customer">Customer</option>
            </select>
          </div>
        )}

        <form onSubmit={handleSubmit(onSubmit, onError)}>
          {/* Name */}
          <div className="mb-3">
            <label className="form-label">Full Name</label>
            <input
              className={`form-control ${errors.name ? "is-invalid" : ""}`}
              {...register("name", { required: "Name is required" })}
            />
            {errors.name && (
              <div className="invalid-feedback">{errors.name.message}</div>
            )}
          </div>

          {/* Email */}
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="email"
              className={`form-control ${errors.email ? "is-invalid" : ""}`}
              {...register("email", {
                required: "Email is required",
                pattern: {
                  value: /^\S+@\S+$/i,
                  message: "Invalid email format",
                },
              })}
            />
            {errors.email && (
              <div className="invalid-feedback">{errors.email.message}</div>
            )}
          </div>

          {/* Password */}
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className={`form-control ${errors.password ? "is-invalid" : ""}`}
              {...register("password", {
                required: "Password is required",
                minLength: {
                  value: 6,
                  message: "Password must be at least 6 characters",
                },
              })}
            />
            {errors.password && (
              <div className="invalid-feedback">{errors.password.message}</div>
            )}
          </div>

          {/* Customer extra fields */}
          {(adminExists || role === "customer") && (
            <>
              <div className="mb-3">
                <label className="form-label">Phone</label>
                <input
                  className={`form-control ${errors.phone ? "is-invalid" : ""}`}
                  {...register("phone", {
                    required: "Phone is required",
                    pattern: {
                      value: /^[0-9]{10}$/,
                      message: "Enter a valid 10-digit number",
                    },
                  })}
                />
                {errors.phone && (
                  <div className="invalid-feedback">{errors.phone.message}</div>
                )}
              </div>

              <div className="mb-3">
                <label className="form-label">Address</label>
                <input
                  className={`form-control ${
                    errors.address ? "is-invalid" : ""
                  }`}
                  {...register("address", { required: "Address is required" })}
                />
                {errors.address && (
                  <div className="invalid-feedback">
                    {errors.address.message}
                  </div>
                )}
              </div>

              <div className="mb-3">
                <label className="form-label">Age</label>
                <input
                  type="number"
                  className={`form-control ${errors.age ? "is-invalid" : ""}`}
                  {...register("age", {
                    required: "Age is required",
                    min: {
                      value: 10,
                      message: "Must be at least 10 years old",
                    },
                    max: { value: 100, message: "Too old for gym üòÖ" },
                  })}
                />
                {errors.age && (
                  <div className="invalid-feedback">{errors.age.message}</div>
                )}
              </div>

              <div className="mb-3">
                <label className="form-label">Gender</label>
                <select
                  className={`form-select ${errors.sex ? "is-invalid" : ""}`}
                  {...register("sex", { required: "Please select gender" })}
                >
                  <option value="">Select</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                </select>
                {errors.sex && (
                  <div className="invalid-feedback">{errors.sex.message}</div>
                )}
              </div>
            </>
          )}

          <button
            type="submit"
            className="btn btn-primary w-100"
            disabled={isSubmitting}
          >
            {isSubmitting ? "Registering..." : "Register"}
          </button>
        </form>

        <p className="mt-3 text-center">
          Already have an account? <a href="/login">Login here</a>
        </p>
      </div>
    </div>
  );
}
