import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  FaUser,
  FaEnvelope,
  FaLock,
  FaPhone,
  FaHome,
  FaBirthdayCake,
  FaDumbbell,
  FaVenusMars,
} from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";

export default function Register() {
  const navigate = useNavigate(() => {
    navigate("/");
  });

  const [adminExists, setAdminExists] = useState(null);
  const [role, setRole] = useState("customer");
  const [message, setMessage] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    const checkAdmin = async () => {
      const res = await fetch("http://localhost:8080/api/admin/exists");
      if (!res.ok) {
        setAdminExists(false);
        return;
      }
      const data = await res.json();
      if (typeof data === "boolean") {
        setAdminExists(data);
        if (data) setRole("customer");
      } else if (typeof data?.data !== "undefined") {
        setAdminExists(data.data);
        if (data.data) setRole("customer");
      } else {
        setAdminExists(false);
      }
    };
    checkAdmin();
  }, []);

  const onSubmit = async (formData) => {
    setMessage(null);
    setSubmitting(true);

    const url =
      role === "admin"
        ? "http://localhost:8080/api/admin/register"
        : "http://localhost:8080/api/customers/register";

    const payload =
      role === "admin"
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
          }
        : {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            phone: formData.phone,
            address: formData.address,
            age: formData.age ? Number(formData.age) : null,
            sex: formData.sex || null,
          };

    const res = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    if (res.ok) {
      const data = await res.json();
      setMessage(data?.message || "Registered successfully");
      reset();
      if (role === "admin") setAdminExists(true);
    } else {
      const errText = await res.text();
      setMessage(errText || "Registration failed");
    }

    setSubmitting(false);
  };

  if (adminExists === null) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div
          className="spinner-border text-primary"
          role="status"
          aria-hidden="true"
        ></div>
        <span className="ms-2 fw-bold text-primary">Checking server...</span>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <button
        className="btn btn-outline-dark"
        onClick={() => {
          navigate("/");
        }}
      >
        Home
      </button>
      <div className="row justify-content-center">
        <div className="col-sm-10 col-md-8 col-lg-6">
          <div className="card shadow-lg border-0 rounded-4">
            <div className="card-body p-5">
              <h3 className="card-title mb-4 text-center fw-bold text-primary">
                <FaDumbbell className="me-2" />
                Gym Registration
              </h3>

              {/* Role Selection */}
              {!adminExists && (
                <div className="mb-4">
                  <label className="form-label fw-semibold">Register as</label>
                  <select
                    className="form-select"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                  >
                    <option value="admin">üèãÔ∏è Admin</option>
                    <option value="customer">üí™ Customer</option>
                  </select>
                </div>
              )}

              <form onSubmit={handleSubmit(onSubmit)} className="row g-3">
                {/* Name */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaUser className="me-2" /> Full Name
                  </label>
                  <input
                    {...register("name", { required: true })}
                    className="form-control"
                    placeholder="John Doe"
                  />
                  {errors.name && (
                    <p className="text-danger">Name is required</p>
                  )}
                </div>

                {/* Email */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaEnvelope className="me-2" /> Email
                  </label>
                  <input
                    type="email"
                    {...register("email", { required: true })}
                    className="form-control"
                    placeholder="example@gym.com"
                  />
                  {errors.email && (
                    <p className="text-danger">Email is required</p>
                  )}
                </div>

                {/* Password */}
                <div className="col-12">
                  <label className="form-label fw-semibold">
                    <FaLock className="me-2" /> Password
                  </label>
                  <input
                    type="password"
                    {...register("password", { required: true })}
                    className="form-control"
                    placeholder="Choose a strong password"
                  />
                  {errors.password && (
                    <p className="text-danger">Password is required</p>
                  )}
                </div>

                {/* Customer Fields */}
                {(adminExists || role === "customer") && (
                  <>
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaPhone className="me-2" /> Phone
                      </label>
                      <input
                        {...register("phone")}
                        className="form-control"
                        placeholder="123-456-7890"
                      />
                    </div>

                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaHome className="me-2" /> Address
                      </label>
                      <input
                        {...register("address")}
                        className="form-control"
                        placeholder="Street, City"
                      />
                    </div>

                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaBirthdayCake className="me-2" /> Age
                      </label>
                      <input
                        type="number"
                        min="0"
                        {...register("age")}
                        className="form-control"
                        placeholder="Age"
                      />
                    </div>

                    {/* Gender (instead of weight) */}
                    <div className="col-md-6">
                      <label className="form-label fw-semibold">
                        <FaVenusMars className="me-2" /> Gender
                      </label>
                      <select {...register("sex")} className="form-select">
                        <option value="">Select</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                      </select>
                    </div>
                  </>
                )}

                {/* Submit Button */}
                <div className="col-12">
                  <button
                    type="submit"
                    className="btn btn-primary w-100 py-2 fw-semibold"
                    disabled={submitting}
                  >
                    {submitting ? (
                      <>
                        <span
                          className="spinner-border spinner-border-sm me-2"
                          role="status"
                          aria-hidden="true"
                        ></span>
                        Registering...
                      </>
                    ) : (
                      `Register ${
                        adminExists || role === "customer"
                          ? "Customer"
                          : "Admin"
                      }`
                    )}
                  </button>
                </div>

                {/* Login Redirect */}
                <div className="text-center mt-3">
                  <p className="mb-0">
                    Already have an account?{" "}
                    <a href="/login" className="fw-bold text-decoration-none">
                      Login here
                    </a>
                  </p>
                </div>
              </form>

              {/* Message */}
              {message && (
                <div className="mt-4">
                  <div className="alert alert-info text-center fw-semibold">
                    {message}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
